export default {
  props: ['controls'],
  data() {
    return {
      headers: [
        { title: 'Status' },
        { title: 'ID' },
        { title: 'Title' },
        { title: 'Severity' },
        { title: 'NIST' }
      ],
      rows: [],
      dtHandle: null
    }
  },
  computed: {
    testBind: function() {
      return store.getBindValue();
    },
    
    /* eslint vue/return-in-computed-property: 0 */
    getControls: function () {
      if (isMounted == true) {
        var val = store.getControls();
        let vm = this;
        // Here's the magic to keeping the DataTable in sync.
        // It must be cleared, new rows added, then redrawn!
        vm.dtHandle.clear();
        val.forEach(function (item) {
          vm.dtHandle.rows.add($(helpers.rowHtml(item))).draw();
        });

        vm.dtHandle.draw();
      }
    }
    /* eslint return-in-computed-property:1 */
  },
  watch: {
    controls(val, oldVal) {
      let vm = this;
      if (isMounted == true) {
        vm.rows = [];
        // You should _probably_ check that this is changed data... but we'll skip that for this example.

        // Here's the magic to keeping the DataTable in sync.
        // It must be cleared, new rows added, then redrawn!
        vm.dtHandle.clear();
        val.forEach(function (item) {
          vm.dtHandle.rows.add($(helpers.rowHtml(item))).draw();
        });

        //vm.dtHandle.rows.add(vm.rows);
        vm.dtHandle.draw();
      }
    }
  },
  mounted() {
    let vm = this;
    var val = store.getControls();
    vm.rows = [];
    // You should _probably_ check that this is changed data... but we'll skip that for this example.

    // Instantiate the datatable and store the reference to the instance in our dtHandle element.
    vm.dtHandle = $('#controls_table').DataTable({
      // Specify whatever options you want, at a minimum these:
      columns: vm.headers,
      //data: vm.rows,
      searching: false,
      paging: true,
      info: false,
      columnDefs: [
        { width: 50, targets: 1}, { width: 600, targets: 2 }
      ],
    });
    val.forEach(function (item) {
      vm.dtHandle.rows.add($(helpers.rowHtml(item))).draw();
    });

    // Array to track the ids of the details displayed rows
    var detailRows = [];

    $('#controls_table tbody').on( 'click', 'tr td.details-control', function () {
        var tr = $(this).closest('tr');
        var td = tr.children('td')[1];
        var row = vm.dtHandle.row( tr );
        var idx = $.inArray( tr.attr('id'), detailRows );

        if ( row.child.isShown() ) {
            tr.removeClass( 'details' );
            row.child.hide();

            // Remove from the 'open' array
            detailRows.splice( idx, 1 );
        }
        else {
            tr.addClass( 'details' );
            row.child( helpers.detailsPanel( store.getControl(td.innerHTML) ) ).show();

            // Add to the 'open' array
            if ( idx === -1 ) {
                detailRows.push( tr.attr('id') );
            }
        }
        Prism.highlightAll();
    } );

    // On each draw, loop over the `detailRows` array and show any child rows
    vm.dtHandle.on( 'draw', function () {
        $.each( detailRows, function ( i, id ) {
            $('#'+id+' td.details-control').trigger( 'click' );
        } );
    } );
    isMounted = true;
  }
}
</script>

